package button

import (
	"log"

	"github.com/canpacis/pacis/components"
	"github.com/canpacis/pacis/html"
)

type keytyp string

type Variant int

func (Variant) Item() {}

const (
	Default = Variant(iota)
	Destructive
	Outline
	Secondary
	Ghost
	Link
)

func (v Variant) Apply(el *html.Element) {
	el.Set("button-variant", v)
}

func (Variant) Done(el *html.Element) {
	v := el.Get("button-variant").(Variant)

	switch v {
	case Default:
		el.AddClass("bg-primary text-primary-foreground shadow hover:bg-primary/90")
	case Destructive:
		el.AddClass("bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90")
	case Outline:
		el.AddClass("border border-input bg-background shadow-sm hover:bg-accent hover:text-accent-foreground")
	case Secondary:
		el.AddClass("bg-secondary text-secondary-foreground hover:bg-secondary/80")
	case Ghost:
		el.AddClass("hover:bg-accent hover:text-accent-foreground")
	case Link:
		el.AddClass("text-primary underline-offset-4 hover:underline")
	default:
		log.Fatalf("invalid button variant: %d", v)
	}
}

type Size int

func (Size) Item() {}

const (
	Md = Size(iota)
	Sm
	Lg
	Icon
)

func (s Size) Apply(el *html.Element) {
	el.Set("button-size", s)
}

func (Size) Done(el *html.Element) {
	s := el.Get("button-size").(Size)

	switch s {
	case Md:
		el.AddClass("h-10 px-4 py-2")
	case Sm:
		el.AddClass("h-9 rounded-md px-3")
	case Lg:
		el.AddClass("h-11 rounded-md px-8")
	case Icon:
		el.AddClass("h-10 w-10")
	default:
		log.Fatalf("invalid button size: %d", s)
	}
}

func New(items ...html.Item) html.Node {
	return html.Button(
		components.ItemsOf(
			items,
			html.Data("slot", "button"),
			html.Class("inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0"),
			Default,
			Md,
		)...,
	)
}

package button

import (
	"log"

	"github.com/canpacis/pacis/components"
	"github.com/canpacis/pacis/html"

	"components/ui/separator"
)

type GroupOrientation int

func (o GroupOrientation) Apply(el *html.Element) {
	el.Set("group-orientation", o)
}

func (GroupOrientation) Done(el *html.Element) {
	o := el.Get("group-orientation").(GroupOrientation)

	switch o {
	case GroupHorizontal:
		el.AddClass("[&>*:not(:first-child)]:rounded-l-none [&>*:not(:first-child)]:border-l-0 [&>*:not(:last-child)]:rounded-r-none")
		el.SetAttribute("data-orientation", "horizontal")
	case GroupVertical:
		el.AddClass("flex-col [&>*:not(:first-child)]:rounded-t-none [&>*:not(:first-child)]:border-t-0 [&>*:not(:last-child)]:rounded-b-none")
		el.SetAttribute("data-orientation", "vertical")
	default:
		log.Fatalf("invalid group orientation: %d", o)
	}
}

func (GroupOrientation) Item() {}

const (
	GroupHorizontal = GroupOrientation(iota)
	GroupVertical
)

func Group(items ...html.Item) html.Node {
	return html.Div(
		components.ItemsOf(
			items,
			html.Role("group"),
			html.Data("slot", "button-group"),
			html.Class("flex w-fit items-stretch has-[>[data-slot=button-group]]:gap-2 [&>*]:focus-visible:relative [&>*]:focus-visible:z-10 has-[select[aria-hidden=true]:last-child]:[&>[data-slot=select-trigger]:last-of-type]:rounded-r-md [&>[data-slot=select-trigger]:not([class*='w-'])]:w-fit [&>input]:flex-1"),
			GroupHorizontal,
		)...,
	)
}

func GroupText(items ...html.Item) html.Node {
	return html.Div(
		components.ItemsOf(
			items,
			html.Data("slot", "button-group-text"),
			html.Class("bg-muted shadow-xs flex items-center gap-2 rounded-md border px-4 text-sm font-medium [&_svg:not([class*='size-'])]:size-4 [&_svg]:pointer-events-none"),
		)...,
	)
}

func GroupSeparator(items ...html.Item) html.Node {
	return separator.New(
		components.ItemsOf(
			items,
			html.Data("slot", "button-group-separator"),
			html.Class("bg-input relative !m-0 self-stretch data-[orientation=vertical]:h-auto"),
		)...,
	)
}
